import React, { Component } from 'react';
import './App.css';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import SignIn from './components/SignIn/SignIn';
import Register from './components/Register/Register';
import Particles from './components/Particles';

const returnRequestOptions = (imageUrl) => {
    const PAT = 'bf70ff63b263462983db5ca8c6c8f6d6';
    const USER_ID = 'aman00001';
    const APP_ID = 'my-first-application-n9ryzl';
    const MODEL_ID = 'face-detection';

    const raw = JSON.stringify({
        "user_app_id": {
            "user_id": USER_ID,
            "app_id": APP_ID
        },
        "inputs": [
            {
                "data": {
                    "image": {
                        "url": imageUrl
                    }
                }
            }
        ]
    });

    const requestOptions = {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Authorization': 'Key ' + PAT
        },
        body: raw
    };

    return requestOptions;
}

class App extends Component {
    constructor() {
        super();
        this.state = {
            input: '',
            imageUrl: '',
            box: {},
            route:"signin"
        }
    }

    onInputChange = (event) => {
        this.setState({ input: event.target.value });
    }

    displayFacebox = (box) => {
        console.log(box);
        this.setState({ box: box });
    }

    calculateFaceLocation = (data) => {
      const image = document.getElementById('inputimage');
      const width = Number(image.width);
      const height = Number(image.height);

      return {
          leftCol: data.leftCol * width,
          topRow: data.topRow * height,
          rightCol: width - data.rightCol * width,
          bottomRow: height - data.bottomRow * height
      }
    }

    onRouteChange = (route) => {
      this.setState({route:route});
    }


    onButtonSubmit = () => {
        console.log('click');
        this.setState({ imageUrl: this.state.input }, () => {
            fetch("https://api.clarifai.com/v2/models/" + 'face-detection' + "/outputs", returnRequestOptions(this.state.imageUrl))
                .then(response => response.json())
                .then(result => {
                    const regions = result.outputs[0].data.regions;

                    if (regions && regions.length > 0) {
                        const region = regions[0];
                        const boundingBox = region.region_info.bounding_box;

                        const faceBox = {
                            topRow: boundingBox.top_row,
                            leftCol: boundingBox.left_col,
                            bottomRow: boundingBox.bottom_row,
                            rightCol: boundingBox.right_col
                        };
                        console.log(faceBox);

                        this.displayFacebox(this.calculateFaceLocation(faceBox));
                    } else {
                        console.log('No regions detected.');
                    }
                })
                .catch(error => {
                    console.log('error', error);
                });
        });
    }

    render() {
        return (

            <div className="App">
                <Particles />
                <Navigation onRouteChange = {this.onRouteChange} route = {this.state.route}/>
                {this.state.route === 'home'
                  ?
                  <div>

                    <Logo />
                    <Rank />
                    <ImageLinkForm
                      onInputChange={this.onInputChange}
                      onButtonSubmit={this.onButtonSubmit}
                    />
                    <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl} />
                  </div> 
                  : 
                  (this.state.route === 'signin'
                    ?
                    <SignIn onRouteChange = {this.onRouteChange}/>
                    :
                    <Register onRouteChange = {this.onRouteChange}/>
                  )
                }
            </div>
        );
    }
}

export default App;
